<?phpini_set('display_errors', '1');ini_set('display_startup_errors', '1');error_reporting(E_ALL);register_shutdown_function(function () {  $e = error_get_last();  if ($e && in_array($e['type'], [E_ERROR, E_PARSE, E_CORE_ERROR, E_COMPILE_ERROR], true)) {    while (ob_get_level()) { @ob_end_clean(); }    header('Content-Type: text/plain; charset=utf-8', true, 500);    echo "WATCHNEXUS FATAL\n";    echo $e['message'] . "\n";    echo $e['file'] . ":" . $e['line'] . "\n";  }});// Run the app normallyrequire __DIR__ . '/index.php';