<?phpdeclare(strict_types=1);require_once __DIR__ . '/../app/bootstrap.php';header('Content-Type: application/json; charset=utf-8');$out = [  'logged_in' => (bool) current_user(),  'user' => current_user(),  'roles' => function_exists('current_roles') ? current_roles() : null,  'is_admin' => function_exists('is_admin') ? is_admin() : null,  'is_mod' => function_exists('is_mod') ? is_mod() : null,  'module_settings_enabled' => function_exists('wnx_module_enabled') ? wnx_module_enabled('settings') : null,];echo json_encode($out, JSON_PRETTY_PRINT);